import React, { useEffect, useState } from 'react'
import AliceCarousel from 'react-alice-carousel';
import 'react-alice-carousel/lib/alice-carousel.css';
import Cards from "./MainApi"

const Carousel = () => {

  const [data, setData] = useState([])

  const item = datamap((value) => {
    if (value.id <= 6) {
      return (
        <div className="main__body-cards">
          <div
            className="main__body-card"
            key={value.id}
            data-aos="fade-up"
            data-aos-anchor-placement="top-bottom"
          >
            <img src={value.img} />
            <h2 className="main_card-title">{value.title}</h2>
            <button
              className="main__card-btn"
              onClick={() => petWindow(value.id)}
            >
              Learn more
            </button>
          </div>
        </div>
      )
    }
  })


  return (
    <div className="carouselBody">
      <AliceCarousel items={item}/>
    </div>
  )
}

export default Carousel
