import React, { useEffect } from 'react'
import { useState } from 'react'

const ToDo = () => {

  const store = localStorage.getItem("myDate")

  const [date, setDate] = useState();
  const [title, setTitle] = useState([])

  const btn = (id) => {
    let newDate = date.filter(filt => filt.id != id)

    setDate(newDate)
  }

  const addInput = (e) => {
    setTitle(e.target.value)
  }

  const add = () => {
    let newDate = {
      id: date.length + 1,
      title : title,
    }

    setDate([newDate, ...date]);
    setTitle("")
  }

  useEffect(() => {
    fetch("https://jsonplaceholder.typicode.com/todos")
    .then(response => response.json())
      .then(info => store ? JSON.parse(store) : setDate)
  }, [])

  localStorage.setItem("myData", JSON.stringify(date))

  return (
    <div className='todo'>
      <div className="todo-head">
        <h2>To Do List</h2>

        <div className="create">
          <input onChange={addInput} value={title} type="text" placeholder='Create...'/>
          <button onClick={add}>Add</button>
        </div>
      </div>

      <div className="todo-body">
        {
          date.map((value) => {
            return(
              <div key={value.id} className='card'>
                <h2>{value.title}</h2>

                <button onClick={() => btn(value.id)}>Delete</button>
              </div>
            )
          })
        }
      </div>
    </div>
  )
}

export default ToDo
